package com.mycompany.exemlolist1;
import java.util.ArrayList; 
import java.util.List;
/**
 *
 * @author lucas
 */
public class ExemloList1 {

    public static void main(String[] args) {
        List <String> carros; 
        carros = new ArrayList();
        carros.add("Monza");
        carros.add("Polo");
        carros.add("Fox");
        carros.add("Gol");
        
        List<Integer> ano;
        ano = new ArrayList();
        ano.add(2002);
        ano.add(2008);
        ano.add(2003);
        ano.add(2007);
        System.out.println("Modelo do carro: "+carros+"\nAno de fabricação: "+ano);
        
    }
}                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               
